package week1.day1;


public class TwoWheeler {
	
	public int noOfWheels = 2;
	public short noOfMirrors = 2;
	public long chasisNumber= 123456789L;
	public boolean isPunture = false;
	public String bikeName = "JAWA";
	public double runningKM = 2305.45;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		TwoWheeler obj = new TwoWheeler();
		
		System.out.println(obj.noOfWheels);
		System.out.println(obj.noOfMirrors);
		System.out.println(obj.chasisNumber);
		System.out.println(obj.isPunture);
		System.out.println(obj.bikeName);
		System.out.println(obj.runningKM);

	}

}
